/* Styles for the Navigation Bar */
        .navigation {
            position: fixed;
            bottom: 0;
            left: 0;
            width: 100%;
            display: flex;
            justify-content: space-around;
            align-items: center;
            padding: 5px 0;
            backdrop-filter: blur(5px);
            background: rgba(12, 51, 72, 0.24);
            height: 120px;
            border-top-left-radius: 20px;
            border-top-right-radius: 20px;
        }

        .nav-link { /* Style the links to look like buttons */
            width: 82px;
            height: 75px;
            border: none;
            cursor: pointer;
            border-radius: 20px;
            overflow: hidden;
            background-color: rgba(255, 255, 255, 0.1);
            background-repeat: no-repeat;
            background-position: center;
            background-size: cover;
            transition: opacity 0.3s ease;
            display: block;  /* Make the link fill the button area */
            text-decoration: none; /* Remove underline */
        }

        /* Specific background images for each link */
        .nav-link:nth-child(1) { background-image: url("https://i.postimg.cc/DZbGJ31y/Untitled-10.png"); } /* Главная */
        .nav-link:nth-child(2) { background-image: url("https://i.postimg.cc/XqNRt1r2/Untitled-4.png"); } /* Задания */
        .nav-link:nth-child(3) { background-image: url("https://i.postimg.cc/59nd8JrD/Untitled-6.png"); } /* Друзья */
        .nav-link:nth-child(4) { background-image: url("https://i.postimg.cc/Zntd6sLP/Untitled-8.png"); } /* Вывод */

        .nav-link:hover {
            opacity: 0.8;
        }

